
let handleText = async function (req, res) {

	/**
	 * @Description Takes the text given in the request object and queries
	 * the recycling information database using a fuzzy text query
	 * @Params 
	 * 	@var req @type {Object} is an object with the request data of the get request
	 * 	@var res @type {Object} is an object with the response functions used to send a response
	 */
	
	// log the program status
  console.log("Querying the database with the following keyword(s): " + req.body.message);

  // connect to mongodb connection
  let db = global.client.db("wastee");
  let items = db.collection("items");

  // initialize queryOutput
  let queryOutput = []

	// double check the request has a message to query with
	if(req.body.message){

		// query the database
		queryOutput = await items.aggregate([
			{
				$search: {
					"compound": {
						"should": [
							{
								"text": {
									"query": req.body.message,
									"path": "name",
									"fuzzy": {
										"maxEdits": 2,
										"maxExpansions": 500
									}
								}
							},
							{
								"text": {
									"query": req.body.message,
									"path": {
										value: "name",
										"multi": "standardAnalyzer"
									},
									"fuzzy": {
										"maxEdits": 2,
										"maxExpansions": 500
									}
								}
							},
							{
								"text": {
									"query": req.body.message,
									"path": "category",
									"fuzzy": {
										"maxEdits": 2,
										"maxExpansions": 500
									}
								}
							},
							{
								"text": {
									"query": req.body.message,
									"path": {
										value: "category",
										"multi": "standardAnalyzer"
									},
									"fuzzy": {
										"maxEdits": 2,
										"maxExpansions": 500
									}
								}
							},
						]
					}
				}
			},
			{
				$limit: 10 // max number of items returned 
			},
			{
				// return the advice and if it is recyclable
				$project: {
					"_id": 0,
					"name": 1,
					"category": 1,
					"recyclable": 1,
					"advice": 1,
					"score": { "$meta": "searchScore" }
				}
			}
		]).toArray()

	}

	// put queryOutput into response object
	res.response.queryOutput = queryOutput

	// predefine outputMessage for worst case scenario
	let outputMessage = "Sorry! We didn't find any recycling information for the description you gave!"
	
	// if an image was given, modify the worst case scenario message to include a reference to the image given
	if(typeof res.response.imageDescription == 'string'){
		outputMessage = "Sorry! We didn't find any recycling information for the image you gave!"
	}
	
	// if the query contains results
	// process the results into user friendly messages
  if(queryOutput.length > 0){

		// change outputMessage to empty string
		outputMessage = ""

		// add image based user friendly message to outputMessage
		if(res.response.imageDescription) {
			outputMessage += "We analyzed your picture and came up with the following description: " + res.response.imageDescription + "\n\n" 
		}
		
		// append user friendly messages for each query result to outputMessage
		for (var i = 0; i < queryOutput.length; i++) {
			outputMessage += "If you're asking about " + queryOutput[i].name + ", which is in the category " + queryOutput[i].category +", this is what we found: \n\n" +
				"Is it recyclable? " + queryOutput[i].recyclable + "."
				if(queryOutput[i].advice) outputMessage += "\n\n" + queryOutput[i].advice
				outputMessage += "\n\n"
		}

  }

	// store outputMessage in the response object
	res.response.info = outputMessage

}

// export the function
module.exports = handleText